/* global describe, it, jsPDF, expect */
/**
 * Standard spec tests
 */

describe("Module: addimage bas64Validation", () => {
  beforeAll(loadGlobals);
  var global =
    (typeof self !== "undefined" && self) ||
    (typeof window !== "undefined" && window) ||
    (typeof global !== "undefined" && global) ||
    Function('return typeof this === "object" && this.content')() ||
    Function("return this")();

  if (global.isNode !== true) {
    it("addImage: base64 validation in use with addImage-Call", () => {
      var doc = new jsPDF();
      var canvas = document.createElement("canvas");
      canvas.width = 100;
      canvas.height = 100;
      var ctx = canvas.getContext("2d");
      var image = ctx.getImageData(0, 0, canvas.width, canvas.height);
      var data = image.data;
      for (var i = 0; i < data.length; i++) {
        data[i] = Math.random() * 255;
      }
      ctx.putImageData(image, 0, 0);

      expect(function() {
        doc.addImage(canvas.toDataURL(), 10, 10);
      }).not.toThrow();
    });
  }

  it("addImage: extractImageFromDataUrl", () => {
    var blackpixel =
      "data:image/jpeg;charset=UTF-8;base64,";

    const doc = new jsPDF("p", "pt", "a4", false);
    const data = doc.__addimage__.extractImageFromDataUrl(blackpixel);
    expect(data.mimeType).toBe("image/jpeg");
    expect(data.data).toBe(
      ""
    );
    expect(doc.__addimage__.extractImageFromDataUrl()).toBe(null);
  });

  it("addImage: validateStringAsBase64", () => {
    var blackpixel =
      "";

    const doc = new jsPDF("p", "pt", "a4", false);
    expect(doc.__addimage__.validateStringAsBase64(blackpixel)).toEqual(true);
    expect(doc.__addimage__.validateStringAsBase64()).toEqual(false);
    expect(doc.__addimage__.validateStringAsBase64("123")).toEqual(false);
    expect(doc.__addimage__.validateStringAsBase64("1234")).toEqual(true);
    expect(doc.__addimage__.validateStringAsBase64("12345")).toEqual(false);
    expect(doc.__addimage__.validateStringAsBase64("سبثلقصثق")).toEqual(false);

    //see issue #2112
    expect(
      doc.__addimage__.validateStringAsBase64(
        "/Content/Images/female.png?id=2bc66217-ba0a-49e5-8195-c97f40e9b3ef"
      )
    ).toEqual(false);
    expect(
      doc.__addimage__.validateStringAsBase64(
        "/Content/Images/employee.png?id=0f10e0cb-b0fc-4e60-ba38-fad827795154"
      )
    ).toEqual(false);
  });
});
