'use strict'
/* global describe, it, jsPDF, comparePdf, expect */
/**
 * Standard spec tests
 */

describe('Plugin: addimage', () => {
	    var originalTimeout;

	    beforeEach(function() {
		originalTimeout = jasmine.DEFAULT_TIMEOUT_INTERVAL;
		jasmine.DEFAULT_TIMEOUT_INTERVAL = 10000;
	    });

	    afterEach(function() {
	      jasmine.DEFAULT_TIMEOUT_INTERVAL = originalTimeout;
	    });

	  it('one blackpixel', (done) => {
		var blackpixel = 'data:image/jpeg;base64,';

	    const doc = new jsPDF('p', 'pt', 'a4', false);
	    doc.addImage(blackpixel, 'JPEG', 15, 40, 1, 1);
	    
	    comparePdf(doc.output(), 'blackpixel.pdf', 'addimage')
	  })
})
